mapper.update:
UPDATE USER_TBL

    NAME=#{name}, NICKNAME=#{nickName}, EMAIL=#{email}, ADDRESS=#{address}, CDATE =#{cDate}

        , PASSWORD=#{password}


WHERE ID = #{id} AND EDATE IS NULL

UPDATE USER_REVIEW_TBL
SET
RATING = #{rating},
REVIEW = #{reviewText},
CDATE = #{cDate}
WHERE IDX = #{reviewIdx}

UPDATE USER_REVIEW_EVALUATION_TBL
EVAL_5001 = GREATEST(EVAL_5001 + #{EVAL_5001}, 0), EVAL_5002 = GREATEST(EVAL_5002 + #{EVAL_5002}, 0), EVAL_5003 = GREATEST(EVAL_5003 + #{EVAL_5003}, 0), EVAL_5004 = GREATEST(EVAL_5004 + #{EVAL_5004}, 0), EVAL_5005 = GREATEST(EVAL_5005 + #{EVAL_5005}, 0), EVAL_5006 = GREATEST(EVAL_5006 + #{EVAL_5006}, 0), EVAL_5007 = GREATEST(EVAL_5007 + #{EVAL_5007}, 0), EVAL_5008 = GREATEST(EVAL_5008 + #{EVAL_5008}, 0), EVAL_5009 = GREATEST(EVAL_5009 + #{EVAL_5009}, 0), EVAL_5010 = GREATEST(EVAL_5010 + #{EVAL_5010}, 0)
WHERE USER_IDX = #{sellerIdx}
