SELECT IDX, SUB_IDX, TITLE, COMPANY, LINK, SDATE, EDATE, PLACE, PRICE, GRADE, CAST, RUNNINGTIME, IMG, ETC
FROM TICKET_TBL



        AND SUB_IDX IN

            #{item}




        AND (TITLE LIKE CONCAT('%', #{query}, '%') OR `CAST` LIKE CONCAT('%', #{query}, '%'))



        AND SDATE <= #{endDate}
        AND EDATE >= #{startDate}



        AND EDATE >= #{today}

WITH RECURSIVE CALENDAR AS ( SELECT DATE(#{startDate}) AS DATE UNION ALL SELECT DATE_ADD(DATE, INTERVAL 1 DAY) FROM CALENDAR c WHERE DATE < #{endDate} ) SELECT DATE_FORMAT(c.DATE, '%Y-%m-%d') AS DATE, SUM(CASE WHEN t.SUB_IDX BETWEEN 3001 AND 3007 THEN 1 ELSE 0 END) AS PERFORMANCE_COUNT, SUM(CASE WHEN t.SUB_IDX = 3008 THEN 1 ELSE 0 END) AS SPORTS_COUNT FROM CALENDAR c LEFT JOIN TICKET_TBL t ON c.DATE BETWEEN t.SDATE AND t.EDATE AND t.EDATE >= #{today}
AND t.SUB_IDX IN
#{item}
GROUP BY c.DATE ORDER BY c.DATE

SELECT IDX, TITLE, COMPANY, LINK, SDATE, EDATE, PLACE, PRICE, GRADE, CAST, RUNNINGTIME, IMG, ETC
FROM TICKET_TBL
WHERE IDX = #{idx}
AND EDATE >= #{today}
